!(function ( $ ) {
    var settings = {};

    $.cookieMessage = function (options) {
        var defaults = {
            mainMessage: "",
            acceptButton: "Accept",
            expirationDays: 20,
            backgroundColor: '#89D4E1',
            fontSize: '16px',
            fontColor: '#2A2D36',
            btnBackgroundColor: 'transparent',
            btnFontSize: '15px',
            btnFontColor: '#2A2D36',
            linkFontColor: '#2A2D36',
            cookieName: 'cookieMessage'
        };

        settings = $.extend( {}, defaults, options );
        ready();
    }

    function ready() {
        var coo = getCookie(settings.cookieName);
        if (coo != "true") {
            $(document).ready(function() {
                cookieMessageGenerate();
            })
        }
    }

    function setCookie(c_name, value, exdays) {
        var exdate = new Date();
        exdate.setDate(exdate.getDate() + exdays);
        var c_value = escape(value) + ((exdays == null) ? "" : "; expires=" + exdate.toUTCString());
        document.cookie = c_name + "=" + c_value;
    }

    function getCookie(c_name) {
        var i, x, y, ARRcookies = document.cookie.split(";");
        for (i = 0; i < ARRcookies.length; i++) {
            x = ARRcookies[i].substr(0, ARRcookies[i].indexOf("="));
            y = ARRcookies[i].substr(ARRcookies[i].indexOf("=") + 1);
            x = x.replace(/^\s+|\s+$/g, "");
            if (x == c_name) {
                return unescape(y);
            }
        }
    }

    function cookieMessageGenerate() {
		var html = '<div id="cookie-msg"><span class="msg">'+settings.mainMessage+'</span><a href="" class="btn-aceptar">'+settings.acceptButton+'</a></div>';

        $("body").append(html);

		setTimeout(function() {
			$("#cookie-msg").css({
				'bottom': '0',
			});
		}, 3000);

        $("#cookie-msg a.btn-aceptar").on("click", function(){
            var coo = setCookie(settings.cookieName, true, settings.expirationDays);

			$("#cookie-msg").css({
				'bottom': '-100%',
			});

            return false;
        });
    }
}( jQuery ));